    public void loseLife() {
        lives--;
        GameFrame.rp.setFigure(lives);
        if (lives == 0) {

        }
    }

    public int getLives() {
        return lives;
    }

    public String getWL() {
        return wins + "/" + losses;
    }

    public void setWL(char x) {
        x = Character.toLowerCase(x);
        if (x == 'w') {
            wins++;
        } else if (x == 'l') {
            losses++;
        } else {
            System.out.println("error wl");
        }
    }

    public void reset() {
        guesses = 0;
        round++;
        lives = 6;
        GameFrame.rp.setFigure(6);
        word = pickWord();
        letters.clear();

        for (int i = 0; i < word.length(); i++) {
            letters.add('_');
        }

    }

    public String pickWord() {
		Random rand = new Random();
        int x = rand.nextInt(wordBank.length);
    	return wordBank[x];
    }
